mapper.select:
select b.no, b.title, b.content, b.hit, date_format(b.reg_date, "%Y-%m-%d") regDate, u.no userNo, u.name userName from users u, board b where u.no = b.user_no
and title like concat('%', #{kwd}, '%')
order by b.no desc limit #{startRowNo}, #{listCnt}

mapper.delete:

delete from  board
where user_no = #{userNo}
and no = #{no}
